import React, { Component } from "react";
import Select from "react-select";
import { getAddress } from "../api/backendApi";

const options = [
  { value: "chocolate", label: "Chocolate" },
  { value: "strawberry", label: "Strawberry" },
  { value: "vanilla", label: "Vanilla" }
];

class SelectOption extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedOption: null,
      address: ""
    };
  }

  handleChange = selectedOption => {
    this.setState({ selectedOption });
    console.log(`Option selected:`, selectedOption);
  };

  async onSearchAddress(value) {
    const result = await getAddress(value);
    this.setState({ position: result });
  }

  handleChangeAddress = elem => {
    console.log("elem", elem.target.value);
    this.setState({ address: elem.target.value });
    this.onSearchAddress(elem.target.value);
  };

  render() {
    const { selectedOption } = this.state;

    return (
      <Select
        value={selectedOption}
        onChange={this.handleChangeAddress}
        options={options}
      />
    );
  }
}

export default SelectOption;
