import React, { Component } from "react";
import { Modal, Button, Card, Row, Col } from "react-bootstrap";
import { withRouter } from "react-router";
import { fetchRequests } from "../api/backendApi";
import './modelUserRequest.scss'

class ModalUserRequest extends Component {
  constructor(props) {
    super(props);
    this.state = {
      requests: []
    };
  }

  async componentDidMount() {
    const { authentication_token, client, email } = localStorage;
    const requests = await fetchRequests(
      authentication_token,
      client,
      email,
      localStorage.user_id
    );
    this.setState({ requests });
  }

  render() {
    return (
      <Modal
        show={this.props.show}
        onHide={this.props.onHide}
        size="lg"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title>Your requests</Modal.Title>
        </Modal.Header>
        <Modal.Body  className={"user_request"}>
          {this.state.requests &&
            this.state.requests.map(request => {
              return (
                <div
                  key={(Math.floor(Math.random() * 10000) + 1).toString()}
                >
                  <Row>
                    <Col xs={12}>
                      <Card className={"requests"}>
                        <Card.Img
                          variant="top"
                          src={`https://source.unsplash.com/user/erondu`}
                          style={{ width: "35%", height: "100%" }}
                        />
                        <Card.Body>
                          <Card.Title>{request.title}</Card.Title>
                          <Card.Subtitle className="mb-2 text-muted">
                            {request.address}
                          </Card.Subtitle>
                          <Card.Text>{request.description}</Card.Text>
                          <Button
                            onClick={() => console.log("here")}
                            variant="primary"
                          >
                            Done
                          </Button>
                        </Card.Body>
                        <Card.Footer variant="secondary" onClick={this.props.onCloseModal}>
                            <Button>
                                Close
                            </Button>
                        </Card.Footer>
                      </Card>
                    </Col>
                  </Row>
                </div>
              );
            })}
        </Modal.Body>
      </Modal>
    );
  }
}

export default withRouter(ModalUserRequest);
