import React, { Component } from "react";
import { Modal, Button, Form } from "react-bootstrap";
import { getAddress } from "../api/backendApi";
import { Select } from "semantic-ui-react";

const countryOptions = [
  { key: "af", value: "af", text: "Afghanistan" },
  { key: "ax", value: "ax", text: "Aland Islands" },
  { key: "al", value: "al", text: "Albania" }
];

class ModalCreateRequest extends Component {
  constructor(props) {
    super(props);
    this.state = {
      requestTitle: "",
      address: "",
      description: "",
      userId: this.props.userId,
      position: {},
      selectedOption: null
    };
  }

  handleChangeRequestTitle = elem => {
    this.setState({ requestTitle: elem.target.value });
  };
  handleChangeDescription = elem => {
    this.setState({ description: elem.target.value });
  };

  async onSearchAddress(value) {
    const result = await getAddress(value);
    console.log("result", result);
    this.setState({ position: result });
  }

  handleChangeAddress = elem => {
    this.setState({ address: elem.target.value });
    this.onSearchAddress(elem.target.value);
  };

  render() {
    return (
      <Modal
        show={this.props.show}
        onHide={this.props.onHide}
        size="lg"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title>Create Request</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Select
            options={countryOptions}
            placeholder="Select your country"
            value={this.state.selectedOption}
          />
          <Form>
            <Form.Group controlId="request-title">
              <Form.Label>Request title</Form.Label>
              <Form.Control
                type="text"
                placeholder="Request title"
                onChange={this.handleChangeRequestTitle}
              />
            </Form.Group>
            <Form.Group controlId="address">
              <Form.Label>Address</Form.Label>
              <Form.Control
                type="text"
                placeholder="Address"
                onChange={this.handleChangeAddress}
              />
            </Form.Group>
            <Form.Group controlId="description">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows="4"
                onChange={this.handleChangeDescription}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={this.props.onCloseModal}>
            Close
          </Button>
          <Button variant="primary" onClick={() => console.log("here")}>
            Submit
          </Button>
        </Modal.Footer>
      </Modal>
    );
  }
}

export default ModalCreateRequest;
